import { babyActions } from '../action-types/index'

const initialState = {
    baby: {
        "list-id": null,
        "baby-name": "",
        "enabled": true
    },
    babies: []
}

export function babiesList(state = initialState, action){
    switch(action.type){
        case babyActions.GET_BABIES:
            return{
                ...state,
                babies: action.payload
            }
        case babyActions.ADD_BABY:
            return{
                ...state,
                baby: {
                    ...baby,
                    "baby-name":  action.payload
                }
            }
        case babyActions.LIST_ID:
            return{
                ...state,
                baby: {
                    ...baby,
                    "list-id": action.payload
                }
            }
        default:
            return state
    }
}

export default babiesList;
